pragma solidity ^0.6.2;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./SatoshiART1155.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract Engine {
    // table with offers
    struct Offer {
        address assetAddress; // address of the token
        SatoshiART1155 asset; // token
        uint256 id; // id (it is an ERC 1155)
        address creator; // who creates the offer
        uint256 price; // price of each token
        bool onSale; // is on sale or not
        uint256 amount; // amount of tokens for sale
        bool isDropOfTheDay; // is the offer a drop of the day?
        uint256 commisionDropOfTheDay; // the commision for the drop of the day. Instead of the standard 2.5% is set by the admin
        bool isAuction; // is this offer for an auction of is a direct sale
    }
    Offer[] public offers;

    // Auctions data. As there will be an auction for celebrities with timing of 10-30 minutes the auction will be handled offchain
    struct Auction {
        address creator; // who creates the auction
        address bestBidder; // the address of the best bidder. Set in the SetWinner call
        uint256 bestBidAmount; // the amount of the best bid. Set in the SetWinner call
        uint256 startTime; // when the auction will start
        uint256 duration; // duration in seconds
        uint256 minimumPrice; // minimum price accepted for the auction
        bool active; // is the auction active or not
    }
    mapping(uint256 => Auction) public auctions;

    event Royalties(address receiver, uint256 amount);
    event Payment(address receiver, uint256 amount);

    // The owner of the token, put to sale some of the tokens
    function putToSale(
        address _assetAddress, // token address
        uint256 _id, // id of the token
        uint256 _amount, // amount of tokens put on sale
        uint256 _price // desired price
    ) public returns (uint256) {
        SatoshiART1155 asset = SatoshiART1155(_assetAddress);
        require(
            asset.balanceOf(msg.sender, _id) >= _amount,
            "You are trying to sale more nfts that the ones you have"
        );

        // Creates the offer, and sets the offer onSale = true
        Offer memory offer =
            Offer({
                asset: SatoshiART1155(_assetAddress),
                assetAddress: _assetAddress,
                creator: msg.sender,
                price: _price,
                id: _id,
                amount: _amount,
                onSale: true,
                isDropOfTheDay: false,
                commisionDropOfTheDay: 0,
                isAuction: false
            });
        offers.push(offer);
        return offers.length - 1;
    }

    function removeFromSale(
        uint256 _offerIndex // id of the offer
    ) public returns (uint256) {
        Offer memory offer = offers[_offerIndex];
        require(
            msg.sender == offer.creator,
            "Only offer creator can remove it from sale"
        );

        offer.onSale = false;
        offers[_offerIndex] = offer;
    }

    // Anyone can buy the tokens, as long the offer is onsale
    function buy(
        uint256 _offerIndex,
        address _to,
        uint256 _amount
    ) public payable {
        Offer memory offer = offers[_offerIndex];
        require(
            offer.isAuction == false,
            "You cannot buy what is on an auction"
        );
        require(offer.onSale == true, "Offer not in sale anymore");
        require(msg.value >= offer.price * _amount, "Not enough funds");
        require(
            offer.asset.balanceOf(offer.creator, offer.id) >= _amount,
            "Owner did not have enough tokens on sale"
        );

        SatoshiART1155 asset = SatoshiART1155(offer.asset);

        // commision structure still not defined
        //    require(asset.getCreator(offer.id) != offer.creator, "Creator of token should be first seller");

        // get tokens owner, who should receive the payment. It is the creator of the offer
        uint256 commision = 9750;
        if (offer.isDropOfTheDay == true)
            commision = 10000 - offer.commisionDropOfTheDay;
        address payable tokensOwner = payable(offer.creator);
        if (asset.getCreator(offer.id) != offer.creator) {
            // transfer the funds to token owner
            tokensOwner.transfer((msg.value * _amount * commision) / 10000);
            emit Payment(
                tokensOwner,
                (msg.value * _amount * commision) / 10000
            );
        } else {
            //transfer the funds to token owner except the royalties, that goes to the creator
            commision = commision - asset.getRoyalties(offer.id);
            tokensOwner.transfer((msg.value * _amount * commision) / 10000);
            emit Payment(
                tokensOwner,
                (msg.value * _amount * commision) / 10000
            );
            address payable artist = payable(offer.creator);
            artist.transfer(
                (msg.value * _amount * asset.getRoyalties(offer.id)) / 10000
            );
            emit Royalties(
                artist,
                (msg.value * _amount * asset.getRoyalties(offer.id)) / 10000
            );
        }
        // transfer the tokens
        offer.asset.safeTransferFrom(offer.creator, _to, offer.id, _amount, "");

        // the sale has been made, so set it to false
        offer.onSale = false;
        offers[_offerIndex] = offer;
    }

    // a token can be set as drop of the day, with a different commision
    function setDropOfTheDay(uint256 _offerIndex, uint256 _commisionInBps)
        public
        payable
    {
        Offer memory offer = offers[_offerIndex];
        offer.isDropOfTheDay = true;
        offer.commisionDropOfTheDay = _commisionInBps;
        offers[_offerIndex] = offer;
    }

    function startAuction(
        address _assetAddress, // token address
        uint256 _id, // id of the token
        uint256 _amount, // amount of tokens put on sale
        uint256 _startDate,
        uint256 _durationInSeconds
    ) public returns (uint256) {
         SatoshiART1155 asset = SatoshiART1155(_assetAddress);
         require(
            asset.balanceOf(msg.sender, _id) >= _amount,
            "You are trying to place in an auction more nfts that the ones you have"
        );

        // Creates the offer, and sets the offer is auction = true
        Offer memory offer =
            Offer({
                asset: SatoshiART1155(_assetAddress),
                assetAddress: _assetAddress,
                creator: msg.sender,
                price: 0,
                id: _id,
                amount: _amount,
                onSale: false,
                isDropOfTheDay: false,
                commisionDropOfTheDay: 0,
                isAuction: true
            });
        offers.push(offer);        

        uint256 _offerIndex = offers.length - 1;

        Auction memory auction =
            Auction({
                creator: msg.sender,
                minimumPrice: 0,
                bestBidAmount: 0,
                startTime: now + _startDate,
                duration: _durationInSeconds,
                bestBidder: address(0),
                active: true
            });
        auctions[_offerIndex] = auction;

        return _offerIndex;
    }
/*
    /////////////////////////// AUCTIONS /////////////////
    function startAuction(
        uint256 _offerIndex,
        uint256 _startDate,
        uint256 _durationInSeconds
    ) public {
        // TODO check that only owner of offer can create an auction for that offer
        Auction memory auction =
            Auction({
                creator: msg.sender,
                minimumPrice: 0,
                bestBidAmount: 0,
                startTime: _startDate,
                duration: _durationInSeconds,
                bestBidder: address(0),
                active: true
            });
        auctions[_offerIndex] = auction;

        Offer memory offer = offers[_offerIndex];
        offer.isAuction = true;
        offers[_offerIndex] = offer;
    }
*/
    // this call is made by the owner to set who wins the auction
    function setWinner(
        uint256 _offerIndex,
        address _winner,
        uint256 _amount
    ) public returns (bool) {
        Auction storage auction = auctions[_offerIndex];
        // TODO check the owner is the one who calls
        require(
            now > auction.startTime + auction.duration,
            "Auction still active"
        );
        require(auction.active == true, "Auction not active anymore");
        require(_amount >= auction.minimumPrice, "Amount not high enough");
        auction.bestBidder = _winner;
        auction.bestBidAmount = _amount;
        auctions[_offerIndex] = auction;
    }

    // The winner of the auction can claim the token
    function claim(uint256 _offerIndex, uint256 _amount) external payable {
        Auction storage auction = auctions[_offerIndex];
        Offer memory offer = offers[_offerIndex];
        require(
            offer.isAuction == true,
            "You cannot claim something that is not in auction"
        );
        require(
            msg.value == auction.bestBidAmount ,
            "Amount is not enough"
        );
        require(msg.sender == auction.bestBidder, "User is not the winner");
        require(
            msg.value >= auction.minimumPrice,
            "amount is lower that the minimum price"
        );
        require(auction.active == true, "Auction not active anymore");
        require(offer.amount == _amount, "The amount of items is different from the ones on the auction");

        SatoshiART1155 asset = SatoshiART1155(offer.asset);

        uint256 commision = 9750;
        if (offer.isDropOfTheDay == true)
            commision = 10000 - offer.commisionDropOfTheDay;
        address payable tokensOwner = payable(offer.creator);
        if (asset.getCreator(offer.id) == offer.creator) {
            // transfer the funds to token owner
            tokensOwner.transfer((msg.value * /*_amount **/ commision) / 10000);
            emit Payment(
                tokensOwner,
                (msg.value * /*_amount **/ commision) / 10000
            );
        } else {
            //transfer the funds to token owner except the royalties, that goes to the creator
            commision = commision - asset.getRoyalties(offer.id);
            tokensOwner.transfer((msg.value * /*_amount **/ commision) / 10000);
            emit Payment(
                tokensOwner,
                (msg.value * /*_amount **/ commision) / 10000
            );
            address payable artist = payable(offer.creator);
            artist.transfer(
                (msg.value * /*_amount **/ asset.getRoyalties(offer.id)) / 10000
            );
            emit Royalties(
                artist,
                (msg.value * /*_amount **/ asset.getRoyalties(offer.id)) / 10000
            );
        }
        // transfer the tokens
        offer.asset.safeTransferFrom(
            offer.creator,
            msg.sender,
            offer.id,
            _amount,
            ""
        );

        // the sale has been made, so set it to false
        offer.onSale = false;
        offers[_offerIndex] = offer;

        auction.active = false;
        auction = auctions[_offerIndex];
    }

    function numOffers() public view returns (uint256) {
        return offers.length - 1;
    }

     function extendAuctionTime(
        uint256 _offerIndex,
        uint256 _durationInSeconds
    ) public returns (bool) {
        Auction storage auction = auctions[_offerIndex];
        require(
            now <= auction.startTime + auction.duration,
            "Auction ended"
        );
        auction.duration = auction.duration + _durationInSeconds;
        auctions[_offerIndex] = auction;
        return true;
    }

}
