/**
 * @title Counters
 * @author Matt Condon (@shrugs)
 * @dev Provides counters that can only be incremented or decremented by one. This can be used e.g. to track the number
 * of elements in a mapping, issuing ERC721 ids, or counting request ids.
 *
 * Include with `using Counters for Counters.Counter;`
 * Since it is not possible to overflow a 256 bit integer with increments of one, `increment` can skip the {SafeMath}
 * overflow check, thereby saving gas. This does assume however correct usage, in that the underlying `_value` is never
 * directly accessed.
 */
library Counters {
    struct Counter {
        // This variable should never be directly accessed by users of the library: interactions must be restricted to
        // the library's function. As of Solidity v0.5.2, this cannot be enforced, though there is a proposal to add
        // this feature: see https://github.com/ethereum/solidity/issues/4637
        uint256 _value; // default: 0
    }

    function current(Counter storage counter) internal view returns (uint256) {
        return counter._value;
    }

    function increment(Counter storage counter) internal {
        // The {SafeMath} overflow check can be skipped here, see the comment at the top
        counter._value += 1;
    }

    function decrement(Counter storage counter) internal {
        counter._value = counter._value - 1;
    }
}

contract SatoshiART1155 is ERC1155, AccessControl {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    struct Token {
        address creator;
        uint256 royalty;
    }
    mapping(uint256 => Token) private _token;

    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");

    bool private _anyAddressCanCreateItem = false;

    event ApprovalForItemCreator(
        address contractOwner,
        address itemCreator,
        bool approved
    );

    constructor() ERC1155("www.satoshi.art/tokens/{id}.json") {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    modifier onlyApprovedItemCreator() {
        require(
            _anyAddressCanCreateItem || hasRole(CREATOR_ROLE, _msgSender()),
            "Address is not approved for creating new item."
        );
        _;
    }

    function setAnyAddressCanCreateItem(bool isAnyAddressCanCreateItem) public {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "Caller is not an admin"
        );
        _anyAddressCanCreateItem = isAnyAddressCanCreateItem;
    }

    function anyAddressCanCreateItem() public view returns (bool) {
        return _anyAddressCanCreateItem;
    }

    function createItem(
        uint256 amount,
        uint256 royalty //multiplier:10000
    ) external onlyApprovedItemCreator returns (uint256[] memory) {
        require(royalty <= 1000, "Royalty is too high");

        uint256[] memory newItemIds = new uint256[](amount); //storage type can be expensive
        uint256[] memory amounts = new uint256[](amount);
        for (uint256 i = 0; i < amount; i++) {
            _tokenIds.increment();
            newItemIds[i] = _tokenIds.current();
            amounts[i] = 1;
        }
        _mintBatch(_msgSender(), newItemIds, amounts, "");

        for (uint256 i = 0; i < newItemIds.length; i++) {
            _token[newItemIds[i]] = Token({
                creator: _msgSender(),
                royalty: royalty
            });
        }

        return newItemIds;
    }

    function tokenCreator(uint256 _id) external view returns (address) {
        return _token[_id].creator;
    }

    function tokenRoyalty(uint256 _id) external view returns (uint256) {
        return _token[_id].royalty;
    }
}

